    private void btnBuscarActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_btnBuscarActionPerformed
    {//GEN-HEADEREND:event_btnBuscarActionPerformed

        jPanelPrincipal.removeAll();
        Pattern pattern = Pattern.compile("^[_A-Za-z0-9-\\+]+(\\.[_A-Za-z0-9-]+)*@"
            + "[A-Za-z0-9-]+(\\.[A-Za-z0-9]+)*(\\.[A-Za-z]{2,})$");
        String gmail = txtBuscar.getText();
        Matcher mather = pattern.matcher(gmail);;
        if (mather.find())
        {
            boolean ban = false;
            if (buscar(general.getBandejaEntrada(), gmail))
            {
                jPanelPrincipal.setBorder(javax.swing.BorderFactory.createTitledBorder("Bandeja de entrada"));
                ban = true;
            } else
            {
                if (buscar(general.getElimados(), gmail))
                {
                    jPanelPrincipal.setBorder(javax.swing.BorderFactory.createTitledBorder("Eliminados"));
                    ban = true;
                } else
                {
                    if (buscar(general.getEnviados(), gmail))
                    {
                        jPanelPrincipal.setBorder(javax.swing.BorderFactory.createTitledBorder("Enviados"));
                        ban = true;
                    } else
                    {
                        if (buscar(general.getSpam(), gmail))
                        {
                            jPanelPrincipal.setBorder(javax.swing.BorderFactory.createTitledBorder("Spam"));
                            ban = true;
                        } else
                        {
                            JLabel mensaje = new JLabel("No hay concidencias");
                            jPanelPrincipal.add(mensaje);
                        }
                    }
                }
            }
            if (ban)
            {
                JButton boton = new JButton((1) + " :" + encontrado.getObj().getCorreoOrigen() + "->" + encontrado.getObj().getCorreoDestino());
                boton.setMaximumSize(new Dimension(50, 16));
                boton.setMinimumSize(new Dimension(20, 20));
                boton.setPreferredSize(new Dimension(120, 30));
                jPanelPrincipal.add(boton);
                boton.addActionListener(new ActionListener()
                    {
                        @Override
                        public void actionPerformed(ActionEvent e)
                        {
                            new VerCorreo(null, true, encontrado).setVisible(true);
                        }
                    });
                }
            } else
            {
                txtBuscar.setText("");
            }
            jPanelPrincipal.updateUI();
    }//GEN-LAST:event_btnBuscarActionPerformed

