    private void btnSalirActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_btnSalirActionPerformed
    {//GEN-HEADEREND:event_btnSalirActionPerformed
        try
        {
            ObjectOutputStream archivo = new ObjectOutputStream(new FileOutputStream("datos.dat"));
            archivo.writeObject(general);
            archivo.close();
        } catch (Exception e)
        {
            System.out.println("No existe el archivo");
        }

        this.dispose();
        System.exit(0);
    }//GEN-LAST:event_btnSalirActionPerformed

    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_btnGuardarActionPerformed
    {//GEN-HEADEREND:event_btnGuardarActionPerformed
        Pattern pattern = Pattern.compile("^[_A-Za-z0-9-\\+]+(\\.[_A-Za-z0-9-]+)*@"
            + "[A-Za-z0-9-]+(\\.[A-Za-z0-9]+)*(\\.[A-Za-z]{2,})$");
        String gmail = "";
        gmail = txtGmail.getText();
        Matcher mather = pattern.matcher(gmail);
        if (mather.find())
        {
            general.setCorreoOrigen(gmail);
            general.setNombre(txtUsuario.getText());
            txtUsuario.setText(general.getNombre());
            txtGmail.setText(general.getCorreoOrigen());
            txtBuscar.setEnabled(true);
            btnBuscar.setEnabled(true);
            btnBandejaEntrada.setEnabled(true);
            btnEliminados.setEnabled(true);
            btnEnviados.setEnabled(true);
            btnEnviar.setEnabled(true);
            btnRecibir.setEnabled(true);
            btnSpameados.setEnabled(true);
            txtGmail.setEditable(false);
            txtUsuario.setEditable(false);
            btnGuardar.setEnabled(false);
        } else
        {
            txtGmail.setText("");
            txtUsuario.setText("");
        }
    }//GEN-LAST:event_btnGuardarActionPerformed

